(function($){JIRA.Events=JIRA.Events||{};(function(){JIRA.Events.NEW_CONTENT_ADDED="newContentAdded";JIRA.Events.NEW_PAGE_ADDED="newPageAdded";$(function(){JIRA.trigger(JIRA.Events.NEW_CONTENT_ADDED,[$(document)]);JIRA.trigger(JIRA.Events.NEW_PAGE_ADDED,[$(document)])});$(document).bind("dialogContentReady",function(e,dialog){JIRA.trigger(JIRA.Events.NEW_CONTENT_ADDED,[dialog.get$popupContent()]);JIRA.trigger(JIRA.Events.NEW_PAGE_ADDED,[dialog.get$popupContent()])});JIRA.bind("contentRefreshed",function(e,context){JIRA.trigger(JIRA.Events.NEW_CONTENT_ADDED,[$(context)])})})()})(AJS.$);